import React from 'react';
export function isFooter(child) {
  return React.isValidElement(child) && Object.prototype.hasOwnProperty.call(child.type, '__MODAL_FOOTER__');
}
export function isHeader(child) {
  return React.isValidElement(child) && Object.prototype.hasOwnProperty.call(child.type, '__MODAL_HEADER__');
}
export function isBody(child) {
  return React.isValidElement(child) && Object.prototype.hasOwnProperty.call(child.type, '__MODAL_BODY__');
}
