import { isKeyTab } from "../keyboard/identifiers";

var TabListener = function TabListener() {
  var _this = this;

  this.isTabPressed = false;
  window.addEventListener('keydown', function (e) {
    return _this.isTabPressed = isKeyTab(e);
  });
  window.addEventListener('mousedown', function () {
    return _this.isTabPressed = false;
  });
};

export var tabListener = new TabListener();
